#pragma config(Motor,  port1,           leftMotor,     tmotorVex393_HBridge, openLoop, driveLeft)
#pragma config(Motor,  port10,          rightMotor,    tmotorVex393_HBridge, openLoop, driveRight)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

/*
* Project Gravenstein
* Wes Ring
*/
void manualControl(void){
	if (abs(vexRT(Ch2)) > 5 || abs(vexRT(Ch3)) > 5){
		startMotor(leftMotor,vexRT(Ch3));
		startMotor(rightMotor,vexRT(Ch2));
	}
	else
	{
		stopMotor(leftMotor);
		stopMotor(rightMotor);
	}
}

task main(){
//	enableCompetitionMode();
repeat(forever){
		manualControl();
	}
}
